def new_row(N, comb):
    if N <= 0:
        return sum(comb) 

    copy = comb[:] 
    for i in range(len(comb)):
        if comb[i] != 0:
            comb[i] = copy[i-5] + copy[i+5] + copy[i+1] + copy[i-1]

    return new_row(N-1, comb)

